import React, { useState } from 'react';
import './Login.css';
import { useHistory } from 'react-router-dom';


async function loginUser(credentials) {

    return fetch('https://dash-auth.herokuapp.com/user/authenticate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(credentials)
      })
        .then(async (data) => data.json())
}

function Login(props) {
    const [username, setUserName] = useState();
    const [password, setPassword] = useState();
    const [message, setMessage] = useState('');
    const [signupstate, setSignupstate] = useState(false);
    async function handleSubmit(e){
        e.preventDefault();
        const token = await loginUser({
            username,
            password
        }); 
        if(token.token){
        props.setToken(token);
        window.location.href = '/dashboard'
        } else {
         setMessage(token.message);   
        }

    }

    function handlesignup() {
        const signupst = [...signupstate]
        signupst = !signupst;
        setSignupstate(signupst);
    }
    return(
        <div className="login-wrapper">
            <h1>Bon voyage</h1>
            <form className="login-entry" onSubmit={(e) => handleSubmit(e)}>
                <label>
                    <p>Username</p>
                    <input type="text" onChange={(e) => setUserName(e.target.value)}/>
                </label>
                <label>
                    <p>Password</p>
                    <input type="password" onChange={(e) => setPassword(e.target.value)}/>
                </label>
                <h1>{message}</h1>
                <div>
                    <button className="buttons" type="Submit">Sign In</button>
                </div>
                <div>
                    <button className="buttons" type="button" onClick={() => handlesignup()}>Sign Up</button>
                </div>
                {signupstate === true &&
                          <div className="signuo-wrapper">
                           <label>Give it try</label>   
                           <form></form>
                          </div>       
                      }
            </form>
        </div>
    )

  

}




export default Login;
