import React, { useState } from 'react';
import './Login.css';
import { useHistory } from 'react-router-dom';


async function loginUser(credentials) {

    return fetch('https://dash-auth.herokuapp.com/user/authenticate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(credentials)
      })
        .then(async (data) => data.json())
}

function Login(props) {
    const [username, setUserName] = useState();
    const [password, setPassword] = useState();
    const [regfirstname, setRegfirstname] = useState();
    const [reglastname, setRegLastname] = useState();
    const [reguser, setReguser] = useState();
    const [regpsswd, setRegpsswd] = useState();
    const [regcnfpwd, setRegcnfpwd] = useState();
    const [message, setMessage] = useState('');
    const [signupstate, setSignupstate] = useState(false);
    async function handleSubmit(e){
        e.preventDefault();
        const token = await loginUser({
            username,
            password
        }); 
        if(token.token){
        props.setToken(token);
        window.location.href = '/dashboard'
        } else {
         setMessage(token.message);   
        }

    }

    function handlesignup() {
        var sgnp = signupstate;
        sgnp = !sgnp;
        setSignupstate(sgnp);
    }
    return(
        <div className="login-wrapper">
            <h1>Bon voyage</h1>
            <form className="login-entry" onSubmit={(e) => handleSubmit(e)}>
                <label>
                    <p>Username</p>
                    <input type="text" onChange={(e) => setUserName(e.target.value)}/>
                </label>
                <label>
                    <p>Password</p>
                    <input type="password" onChange={(e) => setPassword(e.target.value)}/>
                </label>
                <h1>{message}</h1>
                <div>
                    <button className="buttons" type="Submit">Sign In</button>
                </div>
                <div>
                    <button className="buttons" type="button" onClick={() => handlesignup()}>Sign Up</button>
                </div>
            </form>
            {signupstate === true &&
                <div className="signup-wrapper">
                    <h1>User details</h1>   
                           <form>
                             <label>
                               <p>Firstname</p>
                                  <input type="text" onChange={(e) => setRegfirstname(e.target.value)}/>
                              </label>
                              <label>
                               <p>Lastname</p>
                                  <input type="text" onChange={(e) => setRegLastname(e.target.value)}/>
                              </label>
                              <label>
                               <p>Username</p>
                                  <input type="text" onChange={(e) => setUserName(e.target.value)}/>
                              </label>
                              <label>
                                <p>Password</p>
                                  <a href="https://mugilvannan-4.github.io/React-passwd/">Need Help?</a>
                                  <input type="password" onChange={(e) => setRegpsswd(e.target.value)}/>
                              </label>
                              <label>
                                <p>Confirm Password</p>
                                  <input type="password" onChange={(e) => setRegcnfpwd(e.target.value)}/>
                              </label>
                              <div>
                                  <button className="buttons" type="Submit">Register</button>
                              </div>
                           </form>
                </div>       
                }
        </div>
    )

  

}




export default Login;
